import React from 'react'
import heart from '../../images/heart.png'
import {
  compose,
  setPropTypes,
  defaultProps,
  withHandlers,
  withState
} from 'recompose'
import PropTypes from 'prop-types'
import Typography from '@material-ui/core/Typography'

const enhance = compose(
  setPropTypes({
    onClick: PropTypes.func,
    type: PropTypes.button,
    className: PropTypes.func,
    count: PropTypes.number
  }),
  defaultProps({
    type: 'button'
  }),
  withState('count', 'setCount', Math.round(Math.random()*100)),
  withHandlers({
    incrementCount: props => event => {
      event.preventDefault()
      props.setCount(props.count + 1)
    },
    decrementCount: props => event => {
      event.preventDefault()
      props.setCount(props.count - 1)
    }
  })
)

const Button = ({ decrementCount, incrementCount, count }) => {
  return (
    <div>
      <button onClick={incrementCount}>
        <img src={heart} height='15' width='15' />
      </button>
      <button onClick={decrementCount}>
        <img src={heart} height='15' width='15' />
      </button>
      <Typography color='textSecondary'>
        Love'o'Meter: {count}
      </Typography>
    </div>
  )
}

export default enhance(Button)
