import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';

function getModalStyle() {
  const top = 20
  const left = 20

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const styles = theme => ({
  paper: {
    position: 'absolute',
    width: theme.spacing.unit * 50,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
  },
});

class SimpleModal extends React.Component {
  state = {
    open: false,
    response: '',
    message: '',
    responseToMessage: '',
  };

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({response: res.express}))
      .catch(err => console.log(err))
  }

  callApi = async () => {
    const response = await fetch('/api/test');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body
  }

  postTweet = async e => {
    e.preventDefault();
    const response = await fetch('/api/test', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.post }),
    });
    const body = await response.text();
    var newStar = 
      [{
        name: 'Sebi "The Viking"',
        review: 'Alex suffers from inner genital syndrome',
        message: body,
      }] 
    this.setState({
      stars: [...newStar, ...this.state.stars]
    })
    this.setState({responseToPost: body});
  }

  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };


  render() {
    const { classes } = this.props;

    return (
      <div>
        <Button onClick={this.handleOpen}>New Tweet</Button>
        <Modal
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          open={this.state.open}
          onClose={this.handleClose}
        >
          <div style={getModalStyle()} className={classes.paper}>
            <Typography variant="h6" id="modal-title">
              Post a new tweet:
            </Typography>
            {/* <TextField
                id="outlined-multiline-flexible"
                label=""
                multiline
                rowsMax="4"
                value={this.state.multiline}
                className={classes.textField}
                margin="normal"
                helperText=""
                variant="outlined"
        /> */}

          <form onSubmit={this.postTweet}>
            <input type='text' value={this.state.post} onChange={e => this.setState({ post: e.target.value })} />
            <button type='submit'>Post!</button>
          </form>
          </div>
        </Modal>
      </div>
    );
  }
}

SimpleModal.propTypes = {
  classes: PropTypes.object.isRequired,
};

// We need an intermediary variable for handling the recursive nesting.
const SimpleModalWrapped = withStyles(styles)(SimpleModal);

export default SimpleModalWrapped;
