import React from 'react'
import heart from '../../images/heart.png'
import { compose, setPropTypes, defaultProps, withHandlers, withState } from 'recompose'
import PropTypes from 'prop-types'

const enhance = compose(
  setPropTypes({
    onClick: PropTypes.func,
    type: PropTypes.button,
    className: PropTypes.func,
    counting: PropTypes.number
  }),
  defaultProps({
    type: 'button'
  }),
  withState('counting', 'setCounting', 0),
  withHandlers({
    onClick: props => event => {
      event.preventDefault()
      props.setCounting(props.counting + 1)
    }
  })
)

const Button = ({ type, className, onClick, counting }) => {
  return (
    <div>
      <button type={type} className={className} onClick={onClick} counting={0}>
        <img src={heart} height='15' width='15' />
      </button>
      <p>Love'o'meter: {counting}</p>
    </div>
  )
}

export default enhance(Button)
